export type MapDependencies = {
  cultures: Culture[];
  places: Place[];
  beliefs: Belief[];
  professions: AvatarProfession[];
  archetypes: Avatar[];
  avatars: Avatar[];
  cultureNameBases: CultureNameBase[];
};

// TODO: move this to a component
export const getShieldUrl = (coatOfArms: any = {}) => {
  const url = new URL('https://armoria.herokuapp.com');
  url.searchParams.set('size', '28');
  url.searchParams.set('format', 'svg');
  url.searchParams.set('coa', JSON.stringify(coatOfArms));

  return url.href;
};

export const getRelatedAvatars = (
  avatar: Avatar,
  avatars: Avatar[]
): Record<string, Avatar[]> => {
  const relatedAvatars = (avatar.relationships || [])
    .map((relationship) => ({
      kind: relationship.kind,
      avatar: avatars.find(
        (avatar) => avatar.id === relationship.avatar
      ) as Avatar,
    }))
    .reduce<Record<string, Avatar[]>>(
      (acc, { kind, avatar }) => {
        switch (kind) {
          case RelationshipKind.Spouse:
            acc.partners.push(avatar);
            break;
          case RelationshipKind.Parent:
            acc.parents.push(avatar);
            break;
        }
        return acc;
      },
      { partners: [], parents: [], children: [] }
    );

  // TODO: refactor avatars generation to work without the below lines
  relatedAvatars.children = avatars.filter((avatar: Avatar) =>
    avatar.relationships?.some(
      (relationship) =>
        relationship.kind === RelationshipKind.Parent &&
        relationship.avatar === avatar?.id
    )
  );

  return relatedAvatars;
};

export const mapAvatarToAvatarInfo = (
  avatar: Avatar,
  {
    cultures,
    places,
    beliefs,
    professions,
    archetypes,
    avatars,
    cultureNameBases,
  }: MapDependencies
): AvatarInfo => {
  const {
    culture: cultureFMGId,
    religion: religionId,
    archetype: archetypeId,
    birthPlace: birthPlaceId,
  } = avatar.identity || {};
  const currentPlaceId = avatar.location?.burg || birthPlaceId;

  const birthPlace =
    places.find((p: Place) => p.i === birthPlaceId) || places[0];
  const birthPlaceShield = getShieldUrl(birthPlace?.coa || {});
  const currentPlace =
    places.find((p: Place) => p.i === currentPlaceId) || places[0];
  const currentPlaceShield = getShieldUrl(currentPlace?.coa || {});

  const relatedAvatars = getRelatedAvatars(avatar, avatars);

  const culture =
    cultures.find((c: Culture) => c.i === cultureFMGId) || cultures[0];
  const cultureName =
    cultureNameBases.find(
      (cultureBase: CultureNameBase) => cultureBase.i === culture.base
    ) || cultureNameBases[0];

  const cultureId = mapFMGCultureId(cultureName);
  const cultureIcon = cultureIcons[cultureId];

  return {
    avatar,
    birthPlace,
    birthPlaceShield,
    currentPlace,
    currentPlaceShield,
    culture,
    cultureIcon,
    beliefs: beliefs
      .filter((belief) => belief.i === religionId)
      .map((belief) =>
        belief.name === 'No religion' ? { ...belief, name: 'Ateism' } : belief
      ),
    professions: {
      main:
        professions.find((p) => p.id === avatar.identity?.mainProfession) ||
        professions[0],
      secondary:
        professions.find(
          (p) => p.id === avatar.identity?.secondaryProfession
        ) || professions[0],
    },
    archetype: archetypes.find((a) => a.id === archetypeId),
    partners: relatedAvatars.partners || [],
    parents: relatedAvatars.parents || [],
    children: relatedAvatars.children || [],
  };
};
